async function ratkaise(){let t=[[null,null,null],[null,null,null],[null,null,null]];document.querySelectorAll(".cell").forEach((e,l)=>{let n=Number(e.textContent.match(/\d+/)[0]),a=[e.getAttribute("data-position")[0],e.getAttribute("data-position")[2]];t[a[0]][a[1]]=n}),console.clear(),alert("Käytetään algoritmia ratkaisun selvittämiseksi. Klikkaa jatkaaksesi."),await fetch("http://localhost:5000/solve",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({INITIAL_STATE:t})}).then(t=>t.json()).then(t=>{t.forEach((t,e)=>{for(let l=0;l<t.length;l++)for(let n=0;n<t[l].length;n++)if(null===t[l][n]){setTimeout(()=>{document.querySelector(`.cell[data-position="${l}-${n}"]`).dispatchEvent(new Event("mousedown"))},150*e);break}}),setTimeout(()=>{alert("Valmis!")},(t.length+1)*150)})}document.addEventListener("keydown",async function(t){"F2"===t.key&&await ratkaise()}),window.addEventListener("load",()=>{alert("Voit käyttää F2-näppäintä ratkaisun selvittämiseen.")});